import React, { useEffect, useState } from 'react'
import NewsItems from './NewsItems'
import Spinnar from './Spinnar'
import '../App.js'

export default function News(props) {
    
    let [data, setData] = useState([])
    let [loading,setLoading]=useState(false)

  
    const fetchData = async () => {
        try {
            const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=5394c3d0131942a8b6d56d400563313f&page=1&pageSize=20`;
            setLoading(true)
            
            const response = await fetch(url);
            const parsedData = await response.json();

            setData(parsedData.articles)
            setLoading(false)
        }
        catch(error){
            console.log("not fetch the data ",error)
        }

    }


    useEffect(() => {
        fetchData();
    }, []);


    return (
        <div className='container my-3'>
            
            <h1 className='text-center'>Kal-Tak</h1>
            {this.state.loading && <Spinnar />}

            <div className='row'>
                {data.map((item, i) => {
                    return <div className='col-md-4 my-4' key={i}>
                        <NewsItems element={item} />
                    </div>
                })}
            </div>
        </div>
    )
}
