import React, { useState } from 'react';

export default function Signin() {
  const [formState, setFormState] = useState({ name: '', email: '', password: '' });
  const [statusMessage, setStatusMessage] = useState('');

  const onSubmit = async (event) => {
    event.preventDefault();
    const response = await fetch('/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formState)
    });

    const data = await response.json();
    if (data.status === 'success') {
      setStatusMessage('Successfully signed up!');
      setFormState({ name: '', email: '', password: '' });
    } else {
      setStatusMessage('Sign up failed');
    }
  };

  return (
    <div>
      <h1>Sign Up</h1>
      <form onSubmit={onSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={formState.name} onChange={(e) => setFormState({ ...formState, name: e.target.value })} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={formState.email} onChange={(e) => setFormState({ ...formState, email: e.target.value })} />
        </label>
        <label>
          Password:
          <input type="password" name="password" value={formState.password} onChange={(e) => setFormState({ ...formState, password: e.target.value })} />
        </label>
        <button type="submit">Sign Up</button>
      </form>
      {statusMessage && <p>{statusMessage}</p>}
    </div>
  );
}
