import React, { useEffect, useState } from 'react'
import NewsItems from './NewsItems'
import Spinnar from './Spinnar'
import '../App.js'

export default function News(props) {
    
    let [data, setData] = useState([])
    let [loading,setLoading]=useState()
    let[page,setPage]=useState(1)

  
    const fetchData = async () => {
        try {
            const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=5394c3d0131942a8b6d56d400563313f&page=1&pageSize=25`;
            setLoading(true)
            
            const response = await fetch(url);
            const parsedData = await response.json();
            setLoading(false)

            setData(parsedData.articles)
        }
        catch(error){
            console.log("not fetch the data ",error)
        }

    }
    const handlePrivesBtn= async()=>{
        const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=5394c3d0131942a8b6d56d400563313f&page=${page+1}&pageSize=25`;
        setLoading(true)
        
        const response = await fetch(url);
        const parsedData = await response.json();
        setLoading(false)

        setData(parsedData.articles)
       
    }
    const handleNextBtn= async()=>{
        
        const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=5394c3d0131942a8b6d56d400563313f&page=1&pageSize=25`;
            setLoading(true)
            
            const response = await fetch(url);
            const parsedData = await response.json();
            setLoading(false)

            setData(parsedData.articles)
    }


    useEffect(() => {
        fetchData();
    }, []);


    return (
        <div className='container my-3'>
            
            <h1 className='text-center'>Kal-Tak</h1>
            {loading && <Spinnar />}

            <div className='row'>
                {data.map((item, i) => {
                    return <div className='col-md-4 my-4' key={i}>
                        <NewsItems element={item} />
                    </div>
                })}
            </div>
            <div className='container d-flex justify-content-between'>
            <button  type="button" onClick={handlePrivesBtn} class="btn btn-primary">&larr;Prives</button>
            <button type="button" onClick={handleNextBtn} class="btn btn-primary">Next&rarr;</button>
            </div>
        </div>
    )
}
