import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './pages/Navbar';
import News from './pages/News';
import './App.css';

function App() {
  const [language, setLanguage] = useState('en'); // default to English

  return (
    <Router>
      <div className="App">
        <Navbar setLanguage={setLanguage} />
        <Switch>
          <Route exact path="/">
            <News language={language} country="in" category="general" pageSize={10} />
          </Route>
          <Route path="/business">
            <News language={language} country="in" category="business" pageSize={10} />
          </Route>
          <Route path="/entertainment">
            <News language={language} country="in" category="entertainment" pageSize={10} />
          </Route>
          <Route path="/sports">
            <News language={language} country="in" category="sports" pageSize={10} />
          </Route>
          <Route path="/health">
            <News language={language} country="in" category="health" pageSize={10} />
          </Route>
          <Route path="/science">
            <News language={language} country="in" category="science" pageSize={10} />
          </Route>
          <Route path="/technology">
            <News language={language} country="in" category="technology" pageSize={10} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
