import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './signin.css';
import './signup.css';
import './navbar.css'; // New CSS file for navbar specific styles

export default function Navbar() {
    let [inputValue, setInputValue] = useState('');
    let [signinPage, setSigninPage] = useState(false);
    let [signupPage, setSignupPage] = useState(false);
    const [scrolled, setScrolled] = useState(false);

    const handleScroll = () => {
        const offset = window.scrollY;
        if (offset > 50) {
            setScrolled(true);
        } else {
            setScrolled(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const handleSearchBtn = (event) => {
        event.preventDefault();
        setInputValue('');
    };

    return (
        <div>
            <nav className={`navbar navbar-expand-lg navbar-dark bg-dark fixed-top ${scrolled ? 'scrolled' : ''}`}>
                <div className="container-fluid">
                    <Link className="navbar-brand" to="#">Kal-Tak</Link>
